import { useRef, useState } from 'react';
import './App.css';

function App() {

     const [x, setx] = useState([])
     const inputRef = useRef()
     const Add = () => {
      const value = inputRef.current.value
      const newData = {Comleted : false, value }
      setx([...x, newData])
      inputRef.current.value = ""
     }

     const itemdone = (index) => {
      const newx = [...x]
      x[index].Comleted = !x[index].Comleted
      setx(newx)
     }

     console.log(x)

  return (
    <div className="App">
      <h2>To Do List</h2>

      <ul>
        {
          x.map((item, index) => {
            return<li onClick={() => {itemdone(index)}}>{item.value}</li>
          })
        }
      </ul> 

      <input ref = {inputRef} placeholder='Enter New Task.....'></input>
      <button onClick={Add}>Add</button> 
    </div>
  );
}

export default App;
